var getNextVersion = function(postdata, $target){
    var baseUrl = "";
    var response = false;
    $.post(baseUrl + "/admin/files/nextversion", postdata).done( function(resp){
        if(resp.message === 'ok'){
            $target.val(resp.payload.newfilename);
            
        }else{
            console.log(resp.payload.message);
        }
        return resp.payload;
    });
};

